<script>
    var input = document.createElement('label');
    input.id = "share";
    input.style.color = "black";
    input.innerText = "Share this with Friends!"
    const output = document.getElementById('output');
    const shareData = {
        title: 'j5 Quirk',
        text: 'Learn the synth that makes anything possible',
        url: 'https://tokn.site'
    }
    document.body.append(input);
    document.getElementById('share').addEventListener('click', async () => {
        try {
            await navigator.share(shareData);
            input.textContent = 'j5 Quirk shared successfully';
        } catch (err) {
            input.textContent = `Error: ${err}`;
        }
        });

</script>
